pragma solidity ^0.4.24;

import "zeppelin-solidity/contracts/lifecycle/Pausable.sol";

interface ERC20Token {
    function totalSupply() external view returns (uint);
    function balanceOf(address who) external constant returns (uint256);
    function transfer(address to, uint256 value) external returns (bool);
}

contract PlayToken is Pausable{

    ERC20Token Token;

    bool public registeredToken = false;
    uint public tokenDecimalPlaces = 2;
    address public tokenAddress;

    uint public tokenWinRatio = 555555555555555 wei; //1800
    uint public tokenLossRatio = 5000000000000000 wei; //200

    modifier TokenCheck() {
       if (registeredToken != true) { //check balance
            revert();
        }
        _;
    }

    function registerToken(address _tokenAddress, uint decimalPlaces) public onlyOwner{
        Token = ERC20Token(_tokenAddress);
        tokenAddress = _tokenAddress;
        registeredToken = true;
        tokenDecimalPlaces = decimalPlaces;
    }

    function remainingTokens(address who) public constant returns (uint){
        return Token.balanceOf(who)/(10 ** tokenDecimalPlaces);
    }

    function getTokenWinValue(uint betInWei) public view returns(uint){
        return betInWei/tokenWinRatio;
    }

    function getTokenLossValue(uint betInWei) public view returns(uint){
        return betInWei/tokenLossRatio;
    }

    function setWinLossAmount(uint winAmount, uint lossAmount)public onlyOwner{
        tokenWinRatio = 1 ether/winAmount;
        tokenLossRatio = 1 ether/lossAmount;
    }
    function getWinTokenAmount()public constant returns(uint){
        return 1 ether/tokenWinRatio;
    }
    function getLossTokenAmount()public constant returns(uint){
        return 1 ether/tokenLossRatio;
    }

}
