pragma solidity ^0.4.24;

import "zeppelin-solidity/contracts/lifecycle/Pausable.sol";

contract TestMode is Pausable {
    bool public TEST_MODE = false;
    uint public TEST_HOUSE = 111;
    uint public TEST_PLAYER = 111;

    function ToggleTestMode(bool state) public onlyOwner{
      TEST_MODE = state;
    }

    function SetTestModeHands(uint house, uint player) public onlyOwner{
      require(house <= 416 && house >= 1);
      require(player <= 416 && player >= 1);

      TEST_HOUSE = house;
      TEST_PLAYER = player;
    }
}
