pragma solidity 0.4.24;

import "../installed_contracts/oraclize/contracts/usingOraclize.sol";
import "../installed_contracts/stringutils/strings.sol";
import "zeppelin-solidity/contracts/lifecycle/Pausable.sol";
import "zeppelin-solidity/contracts/math/SafeMath.sol";
import "./PlayToken.sol";
import "./CustomEvents.sol";
import "./TestMode.sol";

contract War is usingOraclize, Pausable, PlayToken, CustomEvents, TestMode {
    using strings for *;
    using SafeMath for uint;

    // default game settings
    uint public sideBetPercent_MAX = 80;
    uint public bet_MAX = 10 ether;
    uint public bet_MIN = 0.05 ether;
    uint public gasForShuffle = 185000;

    // player info store
    struct Info{
        address playerAddress;
        uint fees;
        uint mainBetAmount;
        uint sideBetAmount;
        uint sideBetPercentage;
        uint playerHand;
        uint houseHand;
    }

    struct FirstHand{
      bool isTied;
      uint fees;
      uint mainBetAmount;
    }

    mapping (bytes32 => Info) public hand_info;
    mapping (address => FirstHand) public firstHandInfo;
    mapping (address => bool) public hasHandInProgress;
    mapping (address => uint) public refundPool;

    constructor() public {
      OAR = OraclizeAddrResolverI(0x6f485C8BF6fc43eA212E93BBF8ce046C7f1cb475);
      oraclize_setCustomGasPrice(20000000000);
    }
    function() public payable {}

//=====================
// Game Settings
//=====================
    function setGasPrice(uint gasPrice) public onlyOwner{
      oraclize_setCustomGasPrice(gasPrice);
    }
    function setOraclizeGas(uint32 gasAmount) public onlyOwner{
      gasForShuffle = gasAmount;
    }

    function setBetMinMax(uint min_bet, uint max_bet) public onlyOwner{
      require(min_bet > max_bet);
      bet_MIN = min_bet;
      bet_MAX = max_bet;
    }

    function setSideBetPercentage(uint percentage) public onlyOwner{
      require(percentage >= 0 && percentage <= 100);
      sideBetPercent_MAX = percentage;
    }
//=====================
// Main gameplay
//=====================
    function playWar(uint sideBetPercent) public whenNotPaused TokenCheck payable {

      require(sideBetPercent >= 0 && sideBetPercent <= sideBetPercent_MAX);

      require(hasHandInProgress[msg.sender]==false);

      require(msg.value <= bet_MAX && msg.value >= bet_MIN);

      uint betAmount = msg.value;
      uint sideBetAmount = betAmount.mul(sideBetPercent).div(100);
      uint mainBetAmount = betAmount.sub(sideBetAmount);

      emit DEBUG_betSplit(mainBetAmount, sideBetAmount, sideBetPercent);

      //Initiate oraclize call
      bytes32 orcID = oraclize_query("WolframAlpha", "RandomSample[Range[416],2]",gasForShuffle);
      emit currentOrcID(orcID);

      uint orcFees = oraclize_getPrice("WolframAlpha", gasForShuffle);

      //check if previous hand was a tie and player chose to go to WAR
      if(firstHandInfo[msg.sender].isTied){
        assert(msg.value == firstHandInfo[msg.sender].mainBetAmount);
        assert(sideBetAmount == 0);
      }
      else{
        FirstHand memory thisHand = FirstHand({
          isTied : false,
          fees: orcFees,
          mainBetAmount : mainBetAmount
        });
        firstHandInfo[msg.sender] = thisHand;
      }

      Info memory info = Info({
          playerAddress : msg.sender,
          mainBetAmount : mainBetAmount,
          sideBetAmount : sideBetAmount,
          fees: orcFees,
          sideBetPercentage : sideBetPercent,
          playerHand : 0,
          houseHand : 0
      });

      hand_info[orcID] = info;
      refundPool[msg.sender] = msg.value.sub(orcFees);
      hasHandInProgress[msg.sender] = true;
    }

    function parseResult(bytes32 orcID, string result, address playerAddr) private returns(uint, uint){
        strings.slice memory partition = result.toSlice();
        partition.beyond("{".toSlice()).until("}".toSlice());
        strings.slice memory delim = ",".toSlice();

        uint houseRaw = parseInt(partition.split(delim).toString());
        uint playerRaw = parseInt(partition.split(delim).toString());

        if(TEST_MODE){
          houseRaw = TEST_HOUSE;
          playerRaw = TEST_PLAYER;
        }

        hand_info[orcID].houseHand = getCardValue(houseRaw);
        hand_info[orcID].playerHand = getCardValue(playerRaw);

        uint houseHand = hand_info[orcID].houseHand;
        uint playerHand = hand_info[orcID].playerHand;

        emit dealtCards(playerAddr, orcID, houseHand, playerHand, houseRaw, playerRaw);

        return(playerHand, houseHand);
    }

    function __callback(bytes32 orcID, string result) public{
        //security checks
        require(msg.sender == oraclize_cbAddress());
        require(hand_info[orcID].playerAddress!=0x0);

        address playerAddr = hand_info[orcID].playerAddress;
        require(hasHandInProgress[playerAddr]==true);

        hasHandInProgress[playerAddr] = false;
        delete refundPool[playerAddr];

        uint playerHand;
        uint houseHand;

        (playerHand, houseHand) =  parseResult(orcID, result, playerAddr);

        uint fees;

        if(playerHand == houseHand){
            uint betAmount = firstHandInfo[playerAddr].mainBetAmount;
            fees = firstHandInfo[playerAddr].fees;

            if(firstHandInfo[playerAddr].isTied){
                pay(playerAddr, betAmount,fees);
                payToken(playerAddr, getTokenWinValue(betAmount).mul(3));
                delete firstHandInfo[playerAddr];
            }
            else{
                firstHandInfo[playerAddr].isTied = true;

                Info memory hand = hand_info[orcID];

                if(hand.sideBetAmount > 0){
                    sideBetPay(orcID);
                    sideBetPayToken(orcID);
                }
                else{
                    payToken(playerAddr, getTokenLossValue(betAmount));
                }
            }
        }

        uint winAmount = hand_info[orcID].mainBetAmount;
        fees = hand_info[orcID].fees;

        //Pay winner
        if(playerHand > houseHand){
          uint winTokenAmount = getTokenWinValue(winAmount);

          pay(playerAddr,winAmount,fees);
          payToken(playerAddr,winTokenAmount);
          delete firstHandInfo[playerAddr];
          delete hand_info[orcID];
        }
        else if(playerHand < houseHand){
            uint lossTokenAmount = getTokenLossValue(winAmount);

            payToken(playerAddr,lossTokenAmount);
            delete firstHandInfo[playerAddr];
            delete hand_info[orcID];
        }
    }

    function playerSurrender() public whenNotPaused payable {

      if(firstHandInfo[msg.sender].isTied){
          emit DEBUG_str("player surrendering");

        //   uint returnAmount = div(firstHandInfo[msg.sender].mainBetAmount,2);
          uint returnAmount = (firstHandInfo[msg.sender].mainBetAmount).div(2);
          uint fees = firstHandInfo[msg.sender].fees;

          pay(msg.sender,returnAmount,fees);
          delete firstHandInfo[msg.sender];
      }
      else{
        emit DEBUG_str("nothing to surrender"); //change this function to return false
      }

    }
//=====================
// Payment methods
//=====================
    function pay(address _addr, uint amount, uint fees) private {
        amount = amount.sub(fees);
        _addr.transfer(amount);
        emit betPayout(_addr, amount);
    }

    function payToken(address _addr, uint amount) private {
        Token.transfer(_addr,amount * 10 ** tokenDecimalPlaces);
        emit betPayoutToken(_addr, amount);
    }

    function sideBetPay(bytes32 orcID) private{
        Info memory hand = hand_info[orcID];

        if(hand.sideBetAmount > 0) {
          pay(hand.playerAddress,hand.sideBetAmount,hand.fees);
        }
    }

    function sideBetPayToken(bytes32 orcID) private{
        Info memory hand = hand_info[orcID];

        if(hand.sideBetAmount > 0){
          uint sideBetWin = getTokenWinValue(hand.sideBetAmount).mul(10);
          payToken(hand.playerAddress,sideBetWin);
        }
    }

    function refund() whenNotPaused public returns(uint){
      address _addr = msg.sender;
      uint amount = refundPool[_addr];
      require(hasHandInProgress[_addr]==true);
      require(amount>0);
      require(amount<=bet_MAX);

      _addr.transfer(amount);
      emit betPayout(_addr, amount);
      hasHandInProgress[_addr]=false;
      delete refundPool[_addr];
      return amount;
    }

    function houseBankRoll() view public returns(uint){
        return address(this).balance;
    }
//=====================
// Owner
//=====================
    function withdrawFunds() public onlyOwner{
      msg.sender.transfer(address(this).balance);
    }

    function withdrawTokens() public onlyOwner{
      uint amount = Token.balanceOf(address(this));
      Token.transfer(msg.sender, amount);
    }
//=====================
// Special
//=====================
    function getCardValue(uint card) private pure returns(uint){
      if (card > 416 || card < 1) {
        return 0;
      }

      card = card % 52;

      if (card == 0) {
        return 13;
      } else if (card <= 4) {
        return 14;
      } else if (card % 4 > 0) {
        card = card.div(4).add(1);
      } else {
        card = card.div(4);
      }

      return card == 1 ? 14 : card;
    }
}
