pragma solidity 0.4.21;

contract CustomEvents{
    event betPayout(address indexed playerAddr, uint amount);
    event betPayoutToken(address indexed playerAddr, uint amount);
    event dealtCards(address indexed player, bytes32 orcID, uint dealerHand, uint playerHand, uint dealerSuit, uint playerSuit);
    event currentOrcID(bytes32 orcID);

    event DEBUG_betSplit(uint mainBet, uint sideBet, uint percent);
    event DEBUG_str(string msg);
    event DEBUG_invalidOp(string error);
    event DEBUG_addr(address addr);
    event DEBUG_byte(bytes32 thing);
    event DEBUG_uint(uint thing);
}
