    /*function playWar(uint sideBetPercent, uint customDealerVal, uint customPlayerVal) public whenNotPaused payable {*/
    function playWar(uint sideBetPercent) public whenNotPaused TokenCheck payable {

      require(sideBetPercent >= 0 && sideBetPercent <= sideBetPercent_MAX);
      //arequire(hasGameInProgress[msg.sender]==false);

      //ALPHA MAX bet
      require(msg.value <= bet_MAX);

      uint betAmount = msg.value;

      uint sideBetAmount = div(mul(betAmount,sideBetPercent),100);
      uint mainBetAmount = sub(betAmount,sideBetAmount);

      emit DEBUG_betSplit(mainBetAmount, sideBetAmount, sideBetPercent);
      //section

      bool isNewGame = true;

      //check if previous hand was a tie and player chose to go to WAR ***
      if(firstHandInfo[msg.sender].isTied){
        isNewGame = false;
        assert(msg.value == firstHandInfo[msg.sender].mainBetAmount);
      }
      else{
        FirstHand memory thisHand = FirstHand({
          isTied : false,
          mainBetAmount : mainBetAmount
        });

        firstHandInfo[msg.sender] = thisHand;
      }

      //bytes32 orcID = oraclize_query("WolframAlpha", "RandomSample[Range[416],2]",gasForShuffle);
      uint orcID = now;
      DEBUG_uint(orcID);

      //do logs + array store

      Info memory info = Info({
          playerAddress : msg.sender,
          mainBetAmount : mainBetAmount,
          sideBetAmount : sideBetAmount,
          sideBetPercentage : sideBetPercent,
          isNewGame : isNewGame,
          playerHand : 0,
          houseHand : 0
      });

      hand_info[orcID] = info;

      hasGameInProgress[msg.sender] = true;
    }

    function getTokenWinValue(uint betInWei) public returns(uint){
        return betInWei/tokenWinRatio;
    }
    function getTokenLossValue(uint betInWei) public returns(uint){
        return betInWei/tokenLossRatio;
    }

    function parseResult(uint orcID, string result, address playerAddr) returns(uint, uint){
        //parse wolfram shuffle
        strings.slice memory partition = result.toSlice();
        partition.beyond("{".toSlice()).until("}".toSlice());
        strings.slice memory delim = ",".toSlice();
        // string[] memory parts = new string[](2);

        uint houseRaw = parseInt(partition.split(delim).toString());
        uint playerRaw = parseInt(partition.split(delim).toString());

        hand_info[orcID].houseHand = this.getCardValue(houseRaw);
        hand_info[orcID].playerHand = this.getCardValue(playerRaw);

        uint houseHand = hand_info[orcID].houseHand;
        uint playerHand = hand_info[orcID].playerHand;

        //Event for front-end to alert hand finished
        dealtCards(playerAddr, houseHand, playerHand, houseRaw, playerRaw);

        return(2, 1);
    }

    function manualCallback(uint orcID, string result) public payable{
        //security checks
        // if (msg.sender != oraclize_cbAddress()) revert();
