    function playerSurrender() public whenNotPaused payable {

      if(firstHandInfo[msg.sender].isTied){
          DEBUG_str("player surrendering");

          uint returnAmount = div(firstHandInfo[msg.sender].mainBetAmount,2);
          if(pay(msg.sender,returnAmount)){
            delete firstHandInfo[msg.sender];
            // delete hand_info[orcID];
          }
      }
      else{
        DEBUG_str("nothing to surrender"); //change this function to return false
      }

    }

    function setWinLossAmount(uint winAmount, uint lossAmount)public onlyOwner{
        tokenWinRatio = 1 ether/winAmount;
        tokenLossRatio = 1 ether/lossAmount;
    }

    function getWinTokenAmount()public constant returns(uint){
        return 1 ether/tokenWinRatio;
    }

    function getLossTokenAmount()public constant returns(uint){
        return 1 ether/tokenLossRatio;
    }

    function getLossTokenAmount2()public constant returns(uint temp){
        temp = 1 ether/tokenWinRatio;
    }

    function pay(address _addr, uint amount) private returns(bool){
        if(_addr.send(amount)){ //PAY IN ether
          betPayout(_addr, amount);
          return true;
        }
        else{
          //Put in refund?
          DEBUG_str("FATAL: pay failed");
          return false;
        }
    }

    function payToken(address _addr, uint amount) private returns(bool){
        if(Token.transfer(_addr,amount)){ //PAY IN token
          betPayout(_addr, amount);
          return true;
        }
        else{
          //Put in refund?
          DEBUG_str("FATAL: pay in token failed");
          return false;
        }
    }

    function sideBetPay(uint orcID) private{

        Info memory hand = hand_info[orcID];

        if(hand.sideBetAmount > 0){
          DEBUG_str("Side bet won! paying player 1x ETH");
          //pay side bet once and for all + remove sidebet history
        //   uint sideBetWin = mul(hand.sideBetAmount,10);
          pay(hand.playerAddress,hand.sideBetAmount);
        }
    }

    function sideBetPayToken(uint orcID) private{

        Info memory hand = hand_info[orcID];

        if(hand.sideBetAmount > 0){
          DEBUG_str("Side bet won! paying player 10x TOKEN");
          //pay side bet once and for all + remove sidebet history
          uint sideBetWin = mul(getTokenWinValue(hand.sideBetAmount),10);
          payToken(hand.playerAddress,sideBetWin);
        }
    }

    /*Special Sauce*/
    function getCardValue(uint card) public returns(uint){
      if(card > 416 || card < 1){
        return 0;
      }

      if(card > 52){
        card = card % 52;
      }

      if(card == 0){
      	return 13;
      }else if(card <= 4){
        return 14;
      }else if(card % 4 > 0){
        card = add(div(card,4),1);
      }else{
        card = div(card,4);
      }

      if(card == 1){
        return 14;
      }
      else{
        return card;
      }

    }

    function mul(uint a, uint b) internal returns (uint) {
      uint c = a * b;
      assert(a == 0 || c / a == b);
      return c;
    }

    function div(uint a, uint b) internal returns (uint) {
      assert(b > 0);
      uint c = a / b;
      assert(a == b * c + a % b);
      return c;
    }

    function sub(uint a, uint b) internal returns (uint) {
      assert(b <= a);
      return a - b;
    }

    function add(uint a, uint b) internal returns (uint) {
      uint c = a + b;
      assert(c >= a);
      return c;
    }

    function houseBankRoll() constant public returns(uint){
        return this.balance;
    }

    function destroy() onlyOwner public{ //do we need this
      paused = true;
      Pause();
      selfdestruct(owner);
    }
