pragma solidity ^0.4.19;

import "../installed_contracts/oraclize/contracts/usingOraclize.sol";
import "../installed_contracts/stringutils/strings.sol";
import "../installed_contracts/zeppelin/contracts/lifecycle/Pausable.sol";

interface ERC20Token {
    function totalSupply() external view returns (uint);
    function balanceOf(address who) external constant returns (uint256);
    function transfer(address to, uint256 value) external returns (bool);
}

contract War is usingOraclize, Pausable {
    using strings for *;

    address public owner;
    address public manager;
    ERC20Token Token;

    //make these configurablef
    uint public sideBetPercent_MAX = 80;
    uint public bet_MAX = 1 ether;
    bool public registeredToken = false;
    uint public tokenDecimalPlaces = 2;
    uint public tokenWinRatio = 555555555555555 wei; //1800
    uint public tokenLossRatio = 5000000000000000 wei; //200

    uint32 public gasForShuffle; //caculateable?
    uint public serviceFee;

    bool public TEST_MODE = false;
    uint public TEST_HOUSE = 111;
    uint public TEST_PLAYER = 111;

    struct Info{
        address playerAddress;
        uint fees;
        uint mainBetAmount;
        uint sideBetAmount;
        uint sideBetPercentage;
        bool isNewGame;
        uint playerHand;
        uint houseHand;
    }

    mapping (bytes32 => Info) public hand_info;

    mapping (address => bool) public hasGameInProgress;

    struct FirstHand{ //UPDATE FRONT END IF CHANGED, ONLY APPEND AT END
        bool isTied;
        uint fees;
        uint mainBetAmount;
    }

    mapping (address => FirstHand) public firstHandInfo;

    // mapping (string => uint) cards;

    //EVENTS

    event betPayout(address indexed playerAddr, uint amount);
    event betPayoutToken(address indexed playerAddr, uint amount);
    event dealtCards(address indexed player, bytes32 orcID, uint dealerHand, uint playerHand, uint dealerSuit, uint playerSuit);
    event currentOrcID(bytes32 orcID);

    event DEBUG_betSplit(uint mainBet, uint sideBet, uint percent);
    event DEBUG_str(string msg);
    event DEBUG_addr(address addr);
    event DEBUG_byte(bytes32 thing);
    event DEBUG_uint(uint thing);

    modifier TokenCheck() {
       if (registeredToken != true) { //check balance
            revert();
        }
        _;
    }

    function War() public payable{
        //Remove before flight
        /* OAR = OraclizeAddrResolverI(0x6f485C8BF6fc43eA212E93BBF8ce046C7f1cb475); */

        owner = msg.sender;
        manager = msg.sender;

        /* oraclize_setCustomGasPrice(1000000000 wei); //1 GWei */
        gasForShuffle = 180000;
    }

    function ToggleTestMode(bool state) onlyOwner{
      TEST_MODE = state;
    }

    function SetTestModeHands(uint house, uint player) onlyOwner{
      require(house <= 416 && house >= 1);
      require(player <= 416 && player >= 1);

      TEST_HOUSE = house;
      TEST_PLAYER = player;
    }

    function setOraclizeGas(uint32 gasAmount) onlyOwner{
      gasForShuffle = gasAmount;
    }

    function RegisterToken(address _tokenAddress, uint decimalPlaces) public onlyOwner{
        Token = ERC20Token(_tokenAddress);
        DEBUG_str("TOKEN REGISTERED");
        registeredToken = true;
        tokenDecimalPlaces = decimalPlaces;
    }

    function RemainingTokens(address who) public constant returns (uint){
        return Token.balanceOf(who)/(10 ** tokenDecimalPlaces);
    }

    function FundHouse() public payable {}

    function WithdrawFunds() public onlyOwner{
      msg.sender.send(address(this).balance);
    }

    /*function playWar(uint sideBetPercent, uint customDealerVal, uint customPlayerVal) public whenNotPaused payable {*/
    function playWar(uint sideBetPercent) public whenNotPaused TokenCheck payable {

      require(sideBetPercent >= 0 && sideBetPercent <= sideBetPercent_MAX);
      //arequire(hasGameInProgress[msg.sender]==false);

      //ALPHA MAX bet
      require(msg.value <= bet_MAX && msg.value > 0);

      uint betAmount = msg.value;

      uint sideBetAmount = div(mul(betAmount,sideBetPercent),100);
      uint mainBetAmount = sub(betAmount,sideBetAmount);

      emit DEBUG_betSplit(mainBetAmount, sideBetAmount, sideBetPercent);
      //section

      bool isNewGame = true;

      //Initiate oraclize call
      bytes32 orcID = oraclize_query("WolframAlpha", "RandomSample[Range[416],2]",gasForShuffle);
      currentOrcID(orcID);

      uint orcFees = oraclize_getPrice("WolframAlpha", gasForShuffle);
      emit DEBUG_uint(orcFees);

      //do logs + array store

      //check if previous hand was a tie and player chose to go to WAR ***
      if(firstHandInfo[msg.sender].isTied){
        isNewGame = false;
        assert(msg.value == firstHandInfo[msg.sender].mainBetAmount);
        assert(sideBetAmount == 0);
      }
      else{
        FirstHand memory thisHand = FirstHand({
          isTied : false,
          fees: orcFees,
          mainBetAmount : mainBetAmount
        });

        firstHandInfo[msg.sender] = thisHand;
      }

      Info memory info = Info({
          playerAddress : msg.sender,
          mainBetAmount : mainBetAmount,
          sideBetAmount : sideBetAmount,
          fees: orcFees,
          sideBetPercentage : sideBetPercent,
          isNewGame : isNewGame,
          playerHand : 0,
          houseHand : 0
      });

      hand_info[orcID] = info;

      hasGameInProgress[msg.sender] = true;
    }

    function getTokenWinValue(uint betInWei) public returns(uint){
        return betInWei/tokenWinRatio;
    }
    function getTokenLossValue(uint betInWei) public returns(uint){
        return betInWei/tokenLossRatio;
    }
    function setWinLossAmount(uint winAmount, uint lossAmount)public onlyOwner{
        tokenWinRatio = 1 ether/winAmount;
        tokenLossRatio = 1 ether/lossAmount;
    }
    function getWinTokenAmount()public constant returns(uint){
        return 1 ether/tokenWinRatio;
    }
    function getLossTokenAmount()public constant returns(uint){
        return 1 ether/tokenLossRatio;
    }

    function parseResult(bytes32 orcID, string result, address playerAddr) returns(uint, uint){
        //parse wolfram shuffle
        strings.slice memory partition = result.toSlice();
        partition.beyond("{".toSlice()).until("}".toSlice());
        strings.slice memory delim = ",".toSlice();
        // string[] memory parts = new string[](2);

        uint houseRaw = parseInt(partition.split(delim).toString());
        uint playerRaw = parseInt(partition.split(delim).toString());

        if(TEST_MODE){
          houseRaw = TEST_HOUSE;
          playerRaw = TEST_PLAYER;
        }

        hand_info[orcID].houseHand = this.getCardValue(houseRaw);
        hand_info[orcID].playerHand = this.getCardValue(playerRaw);

        uint houseHand = hand_info[orcID].houseHand;
        uint playerHand = hand_info[orcID].playerHand;

        //Event for front-end to alert hand finished
        dealtCards(playerAddr, orcID, houseHand, playerHand, houseRaw, playerRaw);

        return(playerHand, houseHand);
    }

    function __callback(bytes32 orcID, string result) {
        //security checks
        if (msg.sender != oraclize_cbAddress()) revert();
        if (hand_info[orcID].playerAddress==0x0) revert(); //maybe change bytes32 to uint for testing

        //**NEED TO CHECK INPUT FORMAT FOR REFUND

        address playerAddr = hand_info[orcID].playerAddress;

        var (playerHand, houseHand) =  parseResult(orcID, result, playerAddr);

        uint fees;

        if(playerHand == houseHand){
            DEBUG_str("Tied!");

            uint betAmount = firstHandInfo[playerAddr].mainBetAmount;
            fees = firstHandInfo[playerAddr].fees;

            if(firstHandInfo[playerAddr].isTied){
                DEBUG_str("Second Tie - 3x payout!");

                if(pay(playerAddr, betAmount,fees) &&
                payToken(playerAddr, mul(getTokenWinValue(betAmount),3))){
                    delete firstHandInfo[playerAddr];
                }
            }
            else{
                DEBUG_str("First Tie - pending action");
                firstHandInfo[playerAddr].isTied = true;

                Info memory hand = hand_info[orcID];

                if(hand.sideBetAmount > 0){
                    sideBetPay(orcID);
                    sideBetPayToken(orcID);
                }
                else{
                    payToken(playerAddr, getTokenLossValue(betAmount));
                }
            }
            //
        }


        uint winAmount = hand_info[orcID].mainBetAmount;
        fees = hand_info[orcID].fees;

        //Pay winner
        if(playerHand > houseHand){
          uint winTokenAmount = getTokenWinValue(winAmount);

          if(pay(playerAddr,winAmount,fees) && payToken(playerAddr,winTokenAmount)){
              delete firstHandInfo[playerAddr];
              delete hand_info[orcID];
          }
        }
        else if(playerHand < houseHand){
            uint lossTokenAmount = getTokenLossValue(winAmount);

            if(payToken(playerAddr,lossTokenAmount)){
                delete firstHandInfo[playerAddr];
                delete hand_info[orcID];
            }
        }
    }

    function playerSurrender() public whenNotPaused payable {

      if(firstHandInfo[msg.sender].isTied){
          DEBUG_str("player surrendering");

          uint returnAmount = div(firstHandInfo[msg.sender].mainBetAmount,2);
          uint fees = firstHandInfo[msg.sender].fees;

          if(pay(msg.sender,returnAmount,fees)){
            delete firstHandInfo[msg.sender];
            // delete hand_info[orcID];
          }
      }
      else{
        DEBUG_str("nothing to surrender"); //change this function to return false
      }

    }

    function pay(address _addr, uint amount, uint fees) private returns(bool){
        if(_addr.send(amount)){ //PAY IN ether
          betPayout(_addr, amount);
          return true;
        }
        else{
          //Put in refund?
          DEBUG_str("FATAL: pay failed");
          return false;
        }
    }

    function payToken(address _addr, uint amount) private returns(bool){

        if(Token.transfer(_addr,amount * 10 ** tokenDecimalPlaces)){ //PAY IN token
          betPayoutToken(_addr, amount); //actuall number
          return true;
        }
        else{
          //Put in refund?
          DEBUG_str("FATAL: pay in token failed");
          return false;
        }
    }

    function sideBetPay(bytes32 orcID) private{

        Info memory hand = hand_info[orcID];

        if(hand.sideBetAmount > 0){
          DEBUG_str("Side bet won! paying player 1x ETH");
          //pay side bet once and for all + remove sidebet history
        //   uint sideBetWin = mul(hand.sideBetAmount,10);
          pay(hand.playerAddress,hand.sideBetAmount,hand.fees);
        }
    }

    function sideBetPayToken(bytes32 orcID) private{

        Info memory hand = hand_info[orcID];

        if(hand.sideBetAmount > 0){
          DEBUG_str("Side bet won! paying player 10x TOKEN");
          //pay side bet once and for all + remove sidebet history
          uint sideBetWin = mul(getTokenWinValue(hand.sideBetAmount),10);
          payToken(hand.playerAddress,sideBetWin);
        }
    }

    /*Special Sauce*/
    function getCardValue(uint card) public returns(uint){
      if(card > 416 || card < 1){
        return 0;
      }

      if(card > 52){
        card = card % 52;
      }

      if(card == 0){
      	return 13;
      }else if(card <= 4){
        return 14;
      }else if(card % 4 > 0){
        card = add(div(card,4),1);
      }else{
        card = div(card,4);
      }

      if(card == 1){
        return 14;
      }
      else{
        return card;
      }

    }

    function mul(uint a, uint b) internal returns (uint) {
      uint c = a * b;
      assert(a == 0 || c / a == b);
      return c;
    }

    function div(uint a, uint b) internal returns (uint) {
      assert(b > 0);
      uint c = a / b;
      assert(a == b * c + a % b);
      return c;
    }

    function sub(uint a, uint b) internal returns (uint) {
      assert(b <= a);
      return a - b;
    }

    function add(uint a, uint b) internal returns (uint) {
      uint c = a + b;
      assert(c >= a);
      return c;
    }

    function houseBankRoll() constant public returns(uint){
        return address(this).balance;
    }
}
